mapper.select:
select COUNT(*) as total from almacens a
inner join distritos d on a.distrito_id = d.id
inner join provincias p on d.provincia_id = p.id
inner join departamentos de on p.departamento_id = de.id
inner join pais pa on de.pais_id = pa.id

     AND a.id = #{ID}
     AND a.nombre = #{NOMBRE}
     AND a.direccion = #{DIRECCION}
     AND a.empresa_id = #{EMPRESA_ID}
     AND a.user_id = #{USER_ID}
     AND a.activo = #{ACTIVO}
     AND a.borrado = #{BORRADO}
     AND a.distrito_id = #{DISTRITO_ID}
     AND a.codigo = #{CODIGO}

     AND a.id != #{NO_ID}
     AND a.borrado != #{NO_BORRADO}
     AND a.activo != #{NO_ACTIVO}

     AND (a.nombre like #{BUSCAR} or pa.nombre like #{BUSCAR} or  de.nombre like #{BUSCAR} or p.nombre like #{BUSCAR}
                                or  d.nombre like #{BUSCAR} or a.direccion like #{BUSCAR})

order by pa.id, de.id, p.id, d.id,  a.nombre, a.id

select a.id, a.nombre, a.direccion, a.empresa_id, a.user_id, a.activo, a.borrado, a.created_at, a.updated_at, a.distrito_id, a.codigo
, d.id as id_istrito, d.nombre as distrito
, p.id as id_provincia, p.nombre as provincia
, de.id as id_departamento, de.nombre as departamento
, pa.id as id_pais, pa.nombre as pais , pa.inicial as pais_inicial
from almacens a
inner join distritos d on a.distrito_id = d.id
inner join provincias p on d.provincia_id = p.id
inner join departamentos de on p.departamento_id = de.id
inner join pais pa on de.pais_id = pa.id

     AND a.id = #{ID}
     AND a.nombre = #{NOMBRE}
     AND a.direccion = #{DIRECCION}
     AND a.empresa_id = #{EMPRESA_ID}
     AND a.user_id = #{USER_ID}
     AND a.activo = #{ACTIVO}
     AND a.borrado = #{BORRADO}
     AND a.distrito_id = #{DISTRITO_ID}
     AND a.codigo = #{CODIGO}

     AND a.id != #{NO_ID}
     AND a.borrado != #{NO_BORRADO}
     AND a.activo != #{NO_ACTIVO}

     AND (a.nombre like #{BUSCAR} or pa.nombre like #{BUSCAR} or  de.nombre like #{BUSCAR} or p.nombre like #{BUSCAR}
        or  d.nombre like #{BUSCAR} or a.direccion like #{BUSCAR})

order by pa.id, de.id, p.id, d.id,  a.nombre, a.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

select a.id, a.nombre, a.direccion, a.empresa_id, a.user_id, a.activo, a.borrado, a.created_at, a.updated_at, a.distrito_id, a.codigo
, d.id as id_istrito, d.nombre as distrito
, p.id as id_provincia, p.nombre as provincia
, de.id as id_departamento, de.nombre as departamento
, pa.id as id_pais, pa.nombre as pais , pa.inicial as pais_inicial
from almacens a
inner join distritos d on a.distrito_id = d.id
inner join provincias p on d.provincia_id = p.id
inner join departamentos de on p.departamento_id = de.id
inner join pais pa on de.pais_id = pa.id

     AND a.id = #{ID}
     AND a.nombre = #{NOMBRE}
     AND a.direccion = #{DIRECCION}
     AND a.empresa_id = #{EMPRESA_ID}
     AND a.user_id = #{USER_ID}
     AND a.activo = #{ACTIVO}
     AND a.borrado = #{BORRADO}
     AND a.distrito_id = #{DISTRITO_ID}
     AND a.codigo = #{CODIGO}

     AND a.id != #{NO_ID}
     AND a.borrado != #{NO_BORRADO}
     AND a.activo != #{NO_ACTIVO}

     AND (a.nombre like #{BUSCAR} or pa.nombre like #{BUSCAR} or  de.nombre like #{BUSCAR} or p.nombre like #{BUSCAR}
        or  d.nombre like #{BUSCAR} or a.direccion like #{BUSCAR})

order by a.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}
