mapper.select:
select
c.id,
c.nombre,
c.almacen_id,
c.locked_by,
c.last_balanced,
c.last_balanced_by,
c.last_settled_by,
c.last_settled,
c.last_used_by,
c.last_used,
c.estado,
c.is_created_by,
c.is_balanced,
c.last_system_settled,
c.currency_code,
c.activo,
c.borrado,
c.created_at,
c.updated_at,
c.empresa_id,

a.id as al_id,
a.nombre as al_nombre,
a.direccion as al_direccion,
a.empresa_id as al_empresa_id,
a.user_id as al_user_id,
a.activo as al_activo,
a.borrado as al_borrado,
a.distrito_id as al_distrito_id,
a.codigo as al_codigo

from cajas c
inner join almacens a on a.id = c.almacen_id

     AND c.id = #{ID}
     AND c.nombre = #{NOMBRE}
     AND c.almacen_id = #{ALMACEN_ID}
     AND c.locked_by = #{LOCKED_BY}
     AND c.last_balanced = #{LAST_BALANCED}
     AND c.last_balanced_by = #{LAST_BALANCED_BY}
     AND c.last_settled_by = #{LAST_SETTLED_BY}
     AND c.last_settled = #{LAST_SETTLED}
     AND c.last_used_by = #{LAST_USED_BY}
     AND c.last_used = #{LAST_USED}
     AND c.estado = #{ESTADO}
     AND c.is_created_by = #{IS_CREATED_BY}
     AND c.is_balanced = #{IS_BALANCED}
     AND c.last_system_settled = #{LAST_SYSTEM_SETTLED}
     AND c.currency_code = #{CURRENCY_CODE}
     AND c.activo = #{ACTIVO}
     AND c.borrado = #{BORRADO}
     AND c.empresa_id = #{EMPRESA_ID}

     AND c.id != #{NO_ID}
     AND c.borrado != #{NO_BORRADO}
     AND c.activo != #{NO_ACTIVO}


     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}
     AND (a.nombre like #{A_BUSCAR} )


 order by c.id desc 
 order by c.nombre, c.created_at, c.id 

 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

select COUNT(*)
from cajas c
inner join almacens a on a.id = c.almacen_id

     AND c.id = #{ID}
     AND c.nombre = #{NOMBRE}
     AND c.almacen_id = #{ALMACEN_ID}
     AND c.locked_by = #{LOCKED_BY}
     AND c.last_balanced = #{LAST_BALANCED}
     AND c.last_balanced_by = #{LAST_BALANCED_BY}
     AND c.last_settled_by = #{LAST_SETTLED_BY}
     AND c.last_settled = #{LAST_SETTLED}
     AND c.last_used_by = #{LAST_USED_BY}
     AND c.last_used = #{LAST_USED}
     AND c.estado = #{ESTADO}
     AND c.is_created_by = #{IS_CREATED_BY}
     AND c.is_balanced = #{IS_BALANCED}
     AND c.last_system_settled = #{LAST_SYSTEM_SETTLED}
     AND c.currency_code = #{CURRENCY_CODE}
     AND c.activo = #{ACTIVO}
     AND c.borrado = #{BORRADO}
     AND c.empresa_id = #{EMPRESA_ID}

     AND c.id != #{NO_ID}
     AND c.borrado != #{NO_BORRADO}
     AND c.activo != #{NO_ACTIVO}


     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}
     AND (a.nombre like #{A_BUSCAR} )


 order by c.id desc 
 order by c.nombre, c.created_at, c.id
