mapper.select:
select COUNT(*) from clientes c
inner join tipo_documentos t on c.tipo_documento_id=t.id

     AND c.id = #{ID}
     AND c.nombre = #{NOMBRE}
     AND c.tipo_documento_id = #{TIPO_DOCUMENTO_ID}
     AND c.documento = #{DOCUMENTO}
     AND c.direccion = #{DIRECCION}
     AND c.telefono = #{TELEFONO}
     AND c.correo1 = #{CORREO1}
     AND c.correo2 = #{CORREO2}
     AND c.empresa_id = #{EMPRESA_ID}
     AND c.user_id = #{USER_ID}
     AND c.activo = #{ACTIVO}
     AND borrado = #{BORRADO}

     AND c.id != #{NO_ID}
     AND c.borrado != #{NO_BORRADO}
     AND c.activo != #{NO_ACTIVO}

     AND (c.documento like #{BUSCAR} or c.nombre like #{BUSCAR} )

order by c.nombre, c.documento, c.id

select c.id,
c.nombre,
c.tipo_documento_id,
c.documento,
c.direccion,
c.telefono,
c.correo1,
c.correo2,
c.user_id,
c.empresa_id,
c.activo,
c.borrado,
c.created_at,
c.updated_at,
t.id as td_id,
t.tipo as td_tipo,
t.key as td_key,
t.descripcion as td_descripcion,
t.activo as td_activo,
t.borrado as td_borrado,
t.created_at as td_created_at,
t.updated_at as td_updated_at,
t.user_id as td_user_id
from clientes c
inner join tipo_documentos t on c.tipo_documento_id=t.id

     AND c.id = #{ID}
     AND c.nombre = #{NOMBRE}
     AND c.tipo_documento_id = #{TIPO_DOCUMENTO_ID}
     AND c.documento = #{DOCUMENTO}
     AND c.direccion = #{DIRECCION}
     AND c.telefono = #{TELEFONO}
     AND c.correo1 = #{CORREO1}
     AND c.correo2 = #{CORREO2}
     AND c.empresa_id = #{EMPRESA_ID}
     AND c.user_id = #{USER_ID}
     AND c.activo = #{ACTIVO}
     AND borrado = #{BORRADO}

     AND c.id != #{NO_ID}
     AND c.borrado != #{NO_BORRADO}
     AND c.activo != #{NO_ACTIVO}

     AND (c.documento like #{BUSCAR} or c.nombre like #{BUSCAR} )

order by c.nombre, c.documento, c.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}
