mapper.select:
select * from detalle_unidad_productos

     AND id = #{ID}
     AND producto_id = #{PRODUCTO_ID}
     AND unidad_id = #{UNIDAD_ID}
     AND codigo_unidad = #{CODIGO_UNIDAD}
     AND precio = #{PRECIO}
     AND costo_compra = #{COSTO_COMPRA}
     AND almacen_id = #{ALMACEN_ID}
     AND empresa_id = #{EMPRESA_ID}
     AND user_id = #{USER_ID}
     AND activo = #{ACTIVO}
     AND borrado = #{BORRADO}

     AND id != #{NO_ID}
     AND producto_id != #{PRODUCTO_ID}
     AND borrado != #{NO_BORRADO}
     AND activo != #{NO_ACTIVO}

select  u.id as u_id,
        u.nombre as u_nombre,
        u.cantidad as u_cantidad,
        u.abreviatura as u_abreviatura,
        u.activo as u_activo,
        u.borrado as u_borrado,
        ifnull (d.id, '0') as id,
        d.producto_id,
        d.unidad_id,
        ifnull (d.codigo_unidad, '') as codigo_unidad,
        ifnull (d.precio, (#{PRODUCTO.precioUnidad}*u.cantidad)) as precio,
        ifnull (d.costo_compra, (#{PRODUCTO.precioCompra}*u.cantidad)) as costo_compra,
        d.user_id,
        d.empresa_id,
        d.activo,
        d.borrado,
        d.created_at,
        d.updated_at,
        d.almacen_id
from unidads u
left join detalle_unidad_productos d on u.id=d.unidad_id
                         AND d.producto_id = #{PRODUCTO_ID}
                         AND d.almacen_id = #{ALMACEN_ID}
                         AND d.activo = #{ACTIVO}
                         AND d.borrado = #{BORRADO}
                         AND d.borrado != #{NO_BORRADO}
                         AND d.activo != #{NO_ACTIVO}

     AND u.empresa_id = #{EMPRESA_ID}
     AND u.activo = #{ACTIVO}
     AND u.borrado = #{BORRADO}
     AND u.activo != #{NO_ACTIVO}
     AND u.borrado != #{NO_BORRADO}

order by cantidad, nombre, abreviatura, id
