mapper.select:
   select
   e.id,
   e.numero,
   e.fecha,
   e.hora,
   e.proveedor_id,
   e.orden_compra_id,
   e.facturado,
   e.actualizado,
   e.estado,
   e.total_monto,
   e.factura_proveedor_id,
   e.empresa_id,
   e.user_id,
   e.activo,
   e.borrado,
   e.created_at,
   e.updated_at,
   e.almacen_id,

   a.id as al_id,
   a.nombre as al_nombre,
   a.direccion as al_direccion,
   a.empresa_id as al_empresa_id,
   a.user_id as al_user_id,
   a.activo as al_activo,
   a.borrado as al_borrado,
   a.distrito_id as al_distrito_id,
   a.codigo as al_codigo,

   u.id as user_id,
   u.name as user_name,
   u.email as user_email,
   u.tipo_user_id as user_tipo_user_id,
   u.user_id as user_user_id,
   u.empresa_id as user_empresa_id,
   u.activo as user_activo,
   u.borrado as user_borrado,
   u.almacen_id as user_almacen_id,

   du.id as du_id,
   du.nombres as du_nombres,
   du.apellido_paterno as du_apellido_paterno,
   du.apellido_materno as du_apellido_materno,
   du.direccion as du_direccion,
   du.telefono as du_telefono,
   du.tipo_documento_id as du_tipo_documento_id,
   du.documento as du_documento,
   du.email as du_email,
   du.user_id as du_user_id,
   du.user_gestiona_id as du_user_gestiona_id,
   du.empresa_id as du_empresa_id,
   du.activo as du_activo,
   du.borrado as du_borrado,

   p.id as pro_id,
   p.tipo_documento_id as pro_tipo_documento_id,
   p.documento as pro_documento,
   p.nombre as pro_nombre,
   p.direccion as pro_direccion,
   p.telefono as pro_telefono,
   p.anexo as pro_anexo,
   p.celular as pro_celular,
   p.responsable as pro_responsable,
   p.activo as pro_activo,
   p.borrado as pro_borrado,
   p.user_id as pro_user_id,
   p.empresa_id as pro_empresa_id,
   p.created_at as pro_created_at,
   p.updated_at as pro_updated_at,

   f.id as co_id,
   f.tipo_comprobante_id as co_tipo_comprobante_id,
   f.serie as co_serie,
   f.numero as co_numero,
   f.fecha as co_fecha,
   f.estado as co_estado,
   f.observaciones as co_observaciones,
   f.user_id as co_user_id,
   f.empresa_id as co_empresa_id,
   f.activo as co_activo,
   f.borrado as co_borrado,
   f.created_at as co_created_at,
   f.updated_at as co_updated_at,
   f.almacen_id as co_almacen_id,

   FORMAT(@a:= e.total_monto,2) AS importe_total,
   FORMAT(@b:=(SELECT IFNULL(SUM(pp.`monto_real`),0.00) As montoCobrado FROM `pago_proveedors` pp WHERE pp.`entrada_stock_id`=e.`id` and pp.borrado !='1'),2) AS monto_pagado,
   FORMAT(@a-@b,2) AS monto_por_pagar

   from entrada_stocks e
   inner join almacens a on a.id = e.almacen_id
   inner join users u on u.id = e.user_id
   left join datos_users du on du.user_id = u.id
   left join proveedors p on p.id = e.proveedor_id
   left join factura_proveedors f on f.id = e.factura_proveedor_id

    AND e.estado  >  #{ESTADO_MAYOR}
    AND e.id = #{ID}
    AND e.numero = #{NUMERO}
    AND e.fecha = #{FECHA}
    AND e.hora = #{HORA}
    AND e.proveedor_id = #{PROVEEDOR_ID}
    AND e.orden_compra_id = #{ORDEN_COMPRA_ID}
    AND e.facturado = #{FACTURADO}
    AND e.actualizado = #{ACTUALIZADO}
    AND e.estado = #{ESTADO}
    AND e.total_monto = #{TOTAL_MONTO}
    AND e.factura_proveedor_id = #{FACTURA_PROVEEDOR_ID}
    AND e.empresa_id = #{EMPRESA_ID}
    AND e.user_id = #{USER_ID}
    AND e.activo = #{ACTIVO}
    AND e.borrado = #{BORRADO}
    AND e.almacen_id = #{ALMACEN_ID}

    AND e.id != #{NO_ID}
    AND e.borrado != #{NO_BORRADO}
    AND e.activo != #{NO_ACTIVO}

    AND a.id = #{A_ID}
    AND a.nombre = #{A_NOMBRE}
    AND (a.nombre like #{A_BUSCAR} )

    AND u.id = #{U_ID}
    AND u.name = #{U_NAME}
    AND u.email = #{U_EMAIL}
    AND u.tipo_user_id = #{U_TIPO_USER_ID}

    AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

    AND du.id = #{DU_ID}
    AND du.nombres = #{DU_NOMBRES}
    AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
    AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
    AND du.documento = #{DU_DOCUMENTO}

    AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )


    AND p.id = #{PRO_ID}
    AND p.tipo_documento_id = #{PRO_TIPO_DOCUMENTO_ID}
    AND p.documento = #{PRO_DOCUMENTO}
    AND p.nombre = #{PRO_NOMBRE}
    AND p.direccion = #{PRO_DIRECCION}
    AND p.telefono = #{PRO_TELEFONO}
    AND p.anexo = #{PRO_ANEXO}
    AND p.celular = #{PRO_CELULAR}
    AND p.responsable = #{PRO_RESPONSABLE}
    AND p.empresa_id = #{PRO_EMPRESA_ID}
    AND p.user_id = #{PRO_USER_ID}
    AND p.activo = #{PRO_ACTIVO}
    AND p.borrado = #{PRO_BORRADO}

    AND (p.documento like #{PRO_BUSCAR} or p.nombre like #{PRO_BUSCAR} )

    AND f.id = #{CO_ID}
    AND f.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
    AND f.serie = #{CO_SERIE}
    AND f.numero = #{CO_NUMERO}
    AND f.estado = #{CO_ESTADO}
    AND f.fecha = #{CO_FECHA}

    AND ( e.numero like #{BUSCAR_GENERAL} OR
       p.documento like #{BUSCAR_GENERAL} or p.nombre like #{BUSCAR_GENERAL} OR
       CONCAT(f.serie,'-', f.numero) like #{BUSCAR_GENERAL} OR
       u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
       du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )



order by e.id desc 
order by e.fecha, e.hora, e.id 

LIMIT #{LIMIT}
OFFSET #{OFFSET}

select COUNT(*)
from entrada_stocks e
inner join almacens a on a.id = e.almacen_id
inner join users u on u.id = e.user_id
left join datos_users du on du.user_id = u.id
left join proveedors p on p.id = e.proveedor_id
left join factura_proveedors f on f.id = e.factura_proveedor_id

     AND e.estado  >  #{ESTADO_MAYOR}
     AND e.id = #{ID}
     AND e.numero = #{NUMERO}
     AND e.fecha = #{FECHA}
     AND e.hora = #{HORA}
     AND e.proveedor_id = #{PROVEEDOR_ID}
     AND e.orden_compra_id = #{ORDEN_COMPRA_ID}
     AND e.facturado = #{FACTURADO}
     AND e.actualizado = #{ACTUALIZADO}
     AND e.estado = #{ESTADO}
     AND e.total_monto = #{TOTAL_MONTO}
     AND e.factura_proveedor_id = #{FACTURA_PROVEEDOR_ID}
     AND e.empresa_id = #{EMPRESA_ID}
     AND e.user_id = #{USER_ID}
     AND e.activo = #{ACTIVO}
     AND e.borrado = #{BORRADO}
     AND e.almacen_id = #{ALMACEN_ID}

     AND e.id != #{NO_ID}
     AND e.borrado != #{NO_BORRADO}
     AND e.activo != #{NO_ACTIVO}

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}
     AND (a.nombre like #{A_BUSCAR} )

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )


     AND p.id = #{PRO_ID}
     AND p.tipo_documento_id = #{PRO_TIPO_DOCUMENTO_ID}
     AND p.documento = #{PRO_DOCUMENTO}
     AND p.nombre = #{PRO_NOMBRE}
     AND p.direccion = #{PRO_DIRECCION}
     AND p.telefono = #{PRO_TELEFONO}
     AND p.anexo = #{PRO_ANEXO}
     AND p.celular = #{PRO_CELULAR}
     AND p.responsable = #{PRO_RESPONSABLE}
     AND p.empresa_id = #{PRO_EMPRESA_ID}
     AND p.user_id = #{PRO_USER_ID}
     AND p.activo = #{PRO_ACTIVO}
     AND p.borrado = #{PRO_BORRADO}

     AND (p.documento like #{PRO_BUSCAR} or p.nombre like #{PRO_BUSCAR} )

     AND f.id = #{CO_ID}
     AND f.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND f.serie = #{CO_SERIE}
     AND f.numero = #{CO_NUMERO}
     AND f.estado = #{CO_ESTADO}
     AND f.fecha = #{CO_FECHA}

     AND ( e.numero like #{BUSCAR_GENERAL} OR
        p.documento like #{BUSCAR_GENERAL} or p.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(f.serie,'-', f.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )



 order by e.id desc 
 order by e.fecha, e.hora, e.id
