     AND e.estado  >  #{ESTADO_MAYOR}
     AND e.id = #{ID}
     AND e.numero = #{NUMERO}
     AND e.fecha = #{FECHA}
     AND e.fecha  >=   #{FECHA_INI}
     AND e.fecha  <=   #{FECHA_FIN}

     AND e.hora = #{HORA}
     AND e.proveedor_id = #{PROVEEDOR_ID}
     AND e.orden_compra_id = #{ORDEN_COMPRA_ID}
     AND e.facturado = #{FACTURADO}
     AND e.actualizado = #{ACTUALIZADO}

     AND e.estado != #{NO_ESTADO_PAGO}


         AND e.estado = #{ESTADO}


     AND e.total_monto = #{TOTAL_MONTO}
     AND e.factura_proveedor_id = #{FACTURA_PROVEEDOR_ID}
     AND e.empresa_id = #{EMPRESA_ID}
     AND e.user_id = #{USER_ID}
     AND e.activo = #{ACTIVO}
     AND e.borrado = #{BORRADO}
     AND e.almacen_id = #{ALMACEN_ID}

     AND e.id != #{NO_ID}
     AND e.borrado != #{NO_BORRADO}
     AND e.activo != #{NO_ACTIVO}

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}
     AND (a.nombre like #{A_BUSCAR} )

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )


     AND pro.id = #{PRO_ID}
     AND pro.tipo_documento_id = #{PRO_TIPO_DOCUMENTO_ID}
     AND pro.documento = #{PRO_DOCUMENTO}
     AND pro.nombre = #{PRO_NOMBRE}
     AND pro.direccion = #{PRO_DIRECCION}
     AND pro.telefono = #{PRO_TELEFONO}
     AND pro.anexo = #{PRO_ANEXO}
     AND pro.celular = #{PRO_CELULAR}
     AND pro.responsable = #{PRO_RESPONSABLE}
     AND pro.empresa_id = #{PRO_EMPRESA_ID}
     AND pro.user_id = #{PRO_USER_ID}
     AND pro.activo = #{PRO_ACTIVO}
     AND pro.borrado = #{PRO_BORRADO}

     AND (pro.documento like #{PRO_BUSCAR} or pro.nombre like #{PRO_BUSCAR} )

     AND f.id = #{CO_ID}
     AND f.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND f.serie = #{CO_SERIE}
     AND f.numero = #{CO_NUMERO}
     AND f.estado = #{CO_ESTADO}
     AND f.fecha = #{CO_FECHA}

     AND ( e.numero like #{BUSCAR_GENERAL} OR
        p.documento like #{BUSCAR_GENERAL} or p.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(f.serie,'-', f.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )




     AND d.id = #{ID}
     AND d.entrada_stock_id = #{ENTRADA_STOCK_ID}
     AND d.producto_id = #{PRODUCTO_ID}
     AND d.cantidad = #{CANTIDAD}
     AND d.costo = #{COSTO}
     AND d.almacen_id = #{ALMACEN_ID}
     AND d.cantreal = #{CANTREAL}
     AND d.costo_total = #{COSTO_TOTAL}
     AND d.unidad = #{UNIDAD}
     AND d.lote_id = #{LOTE_ID}
     AND d.empresa_id = #{EMPRESA_ID}
     AND d.user_id = #{USER_ID}
     AND d.activo = #{ACTIVO}
     AND d.borrado = #{BORRADO}

     AND d.id != #{NO_ID}
     AND d.borrado != #{NO_BORRADO}
     AND d.activo != #{NO_ACTIVO}


 order by e.id desc 
 order by e.fecha, e.hora, e.id 

, d.entrada_stock_id, d.id

 LIMIT #{LIMIT}
 OFFSET #{OFFSET}
