mapper.select:
select COUNT(*) from init_comprobantes i
    inner join
    tipo_comprobantes t on t.id = i.tipo_comprobante_id
    left join
    almacens a on a.id = i.almacen_id

     AND i.id = #{ID}
     AND i.num_serie = #{NUM_SERIE}
     AND i.letra_serie = #{LETRA_SERIE}
     AND i.numero = #{NUMERO}
     AND i.numero_actual = #{NUMERO_ACTUAL}
     AND i.cantidad_digitos = #{CANTIDAD_DIGITOS}
     AND i.tipo_comprobante_id = #{TIPO_COMPROBANTE_ID}

     AND i.user_id = #{USER_ID}
     AND i.empresa_id = #{EMPRESA_ID}
     AND i.activo = #{ACTIVO}
     AND i.borrado = #{BORRADO}

     AND i.id != #{NO_ID}
     AND i.borrado != #{NO_BORRADO}
     AND i.activo != #{NO_ACTIVO}

     AND ( i.almacen_id = #{ALMACEN_ID_GENERAL}
         OR i.almacen_id = #{ALMACEN_ID}
        )


order by i.tipo_comprobante_id, i.letra_serie, i.num_serie, i.id

select
    i.id,
    i.num_serie,
    i.letra_serie,
    i.numero,
    i.numero_actual,
    i.cantidad_digitos,
    i.tipo_comprobante_id,
    i.empresa_id,
    i.user_id,
    i.created_at,
    i.updated_at,
    i.activo,
    i.borrado,
    i.almacen_id,

    t.nombre as tipo_comprobante_nombre,
    t.prefix as tipo_comprobante_prefix,
    t.para_venta as tipo_comprobante_para_venta,
    t.activo as tipo_comprobante_activo,

    ifnull(a.id, 0) as al_id,
    ifnull(a.nombre, 'GENERAL (TODOS LOS LOCALES)') as al_nombre,
    ifnull(a.direccion, '') as al_direccion,
    ifnull(a.empresa_id, 0) as al_empresa_id,
    a.user_id as al_user_id,
    a.activo as al_activo,
    a.borrado as al_borrado,
    a.distrito_id as al_distrito_id,
    a.codigo as al_codigo

from init_comprobantes i
inner join
tipo_comprobantes t on t.id = i.tipo_comprobante_id
left join
almacens a on a.id = i.almacen_id

     AND i.id = #{ID}
     AND i.num_serie = #{NUM_SERIE}
     AND i.letra_serie = #{LETRA_SERIE}
     AND i.numero = #{NUMERO}
     AND i.numero_actual = #{NUMERO_ACTUAL}
     AND i.cantidad_digitos = #{CANTIDAD_DIGITOS}
     AND i.tipo_comprobante_id = #{TIPO_COMPROBANTE_ID}

     AND i.user_id = #{USER_ID}
     AND i.empresa_id = #{EMPRESA_ID}
     AND i.activo = #{ACTIVO}
     AND i.borrado = #{BORRADO}

     AND i.id != #{NO_ID}
     AND i.borrado != #{NO_BORRADO}
     AND i.activo != #{NO_ACTIVO}

     AND ( i.almacen_id = #{ALMACEN_ID_GENERAL}
         OR i.almacen_id = #{ALMACEN_ID}
        )


order by i.tipo_comprobante_id, i.letra_serie, i.num_serie, i.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}
