mapper.select:
select * from lotes

     AND id = #{ID}
     AND nombre = #{NOMBRE}
     AND fecha_ingreso = #{FECHA_INGRESO}
     AND fecha_vencimiento = #{FECHA_VENCIMIENTO}
     AND activo_vencimiento = #{ACTIVO_VENCIMIENTO}
     AND producto_id = #{PRODUCTO_ID}
     AND cantidad = #{CANTIDAD}
     AND observacion = #{OBSERVACION}
     AND almacen_id = #{ALMACEN_ID}
     AND unidad_id = #{UNIDAD_ID}
     AND empresa_id = #{EMPRESA_ID}
     AND user_id = #{USER_ID}
     AND activo = #{ACTIVO}
     AND borrado = #{BORRADO}

     AND id != #{NO_ID}
     AND borrado != #{NO_BORRADO}
     AND activo != #{NO_ACTIVO}

     AND cantidad  >=  #{CANTIDAD_MIN}

order by orden, id

select COUNT(*) from lotes

     AND id = #{ID}
     AND nombre = #{NOMBRE}
     AND fecha_ingreso = #{FECHA_INGRESO}
     AND fecha_vencimiento = #{FECHA_VENCIMIENTO}
     AND activo_vencimiento = #{ACTIVO_VENCIMIENTO}
     AND producto_id = #{PRODUCTO_ID}
     AND cantidad = #{CANTIDAD}
     AND observacion = #{OBSERVACION}
     AND almacen_id = #{ALMACEN_ID}
     AND unidad_id = #{UNIDAD_ID}
     AND empresa_id = #{EMPRESA_ID}
     AND user_id = #{USER_ID}
     AND activo = #{ACTIVO}
     AND borrado = #{BORRADO}

     AND id != #{NO_ID}
     AND borrado != #{NO_BORRADO}
     AND activo != #{NO_ACTIVO}
