f.id as entrada_stock_co_id, f.tipo_comprobante_id as entrada_stock_co_tipo_comprobante_id, f.serie as entrada_stock_co_serie, f.numero as entrada_stock_co_numero, f.fecha as entrada_stock_co_fecha, f.estado as entrada_stock_co_estado, f.observaciones as entrada_stock_co_observaciones, f.user_id as entrada_stock_co_user_id, f.empresa_id as entrada_stock_co_empresa_id, f.activo as entrada_stock_co_activo, f.borrado as entrada_stock_co_borrado, f.created_at as entrada_stock_co_created_at, f.updated_at as entrada_stock_co_updated_at, f.almacen_id as entrada_stock_co_almacen_id
from pago_proveedors pp inner join metodos_pagos m on m.id = pp.metodos_pago_id inner join entrada_stocks e on e.id = pp.entrada_stock_id inner join almacens a on a.id = e.almacen_id inner join users u on u.id = e.user_id left join datos_users du on du.user_id = u.id left join proveedors p on p.id = e.proveedor_id left join factura_proveedors f on f.id = e.factura_proveedor_id
AND pp.id = #{ID} AND pp.entrada_stock_id = #{ENTRADA_STOCK_ID} AND pp.fecha = #{FECHA} AND pp.metodos_pago_id = #{METODOS_PAGO_ID} AND pp.monto_pago = #{MONTO_PAGO} AND pp.monto_real = #{MONTO_REAL} AND pp.tipo_pago = #{TIPO_PAGO} AND pp.tipo_tarjeta = #{TIPO_TARJETA} AND pp.sigla_tarjeta = #{SIGLA_TARJETA} AND pp.numero_tarjeta = #{NUMERO_TARJETA} AND pp.banco = #{BANCO} AND pp.numero_cuenta = #{NUMERO_CUENTA} AND pp.numero_celular = #{NUMERO_CELULAR} AND pp.numero_cheque = #{NUMERO_CHEQUE} AND pp.codigo_operacion = #{CODIGO_OPERACION} AND pp.user_id = #{USER_ID} AND pp.empresa_id = #{EMPRESA_ID} AND pp.activo = #{ACTIVO} AND pp.borrado = #{BORRADO}
AND pp.id != #{NO_ID} AND pp.borrado != #{NO_BORRADO} AND pp.activo != #{NO_ACTIVO}
AND pp.monto_real > #{MAYOR_MIN_IMPORTE}
order by pp.fecha, pp.id LIMIT #{LIMIT} OFFSET #{OFFSET}

select COUNT(*) from pago_proveedors pp
inner join metodos_pagos m on m.id = pp.metodos_pago_id
inner join  entrada_stocks e on e.id = pp.entrada_stock_id
inner join almacens a on a.id = e.almacen_id
inner join users u on u.id = e.user_id
left join datos_users du on du.user_id = u.id
left join proveedors p on p.id = e.proveedor_id
left join factura_proveedors f on f.id = e.factura_proveedor_id

     AND pp.id = #{ID}
     AND pp.entrada_stock_id = #{ENTRADA_STOCK_ID}
     AND pp.fecha = #{FECHA}
     AND pp.metodos_pago_id = #{METODOS_PAGO_ID}
     AND pp.monto_pago = #{MONTO_PAGO}
     AND pp.monto_real = #{MONTO_REAL}
     AND pp.tipo_pago = #{TIPO_PAGO}
     AND pp.tipo_tarjeta = #{TIPO_TARJETA}
     AND pp.sigla_tarjeta = #{SIGLA_TARJETA}
     AND pp.numero_tarjeta = #{NUMERO_TARJETA}
     AND pp.banco = #{BANCO}
