inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id

     AND p.id = #{ID}
     AND p.nombre = #{NOMBRE}
     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.stock_minimo = #{STOCK_MINIMO}
     AND p.precio_unidad = #{PRECIO_UNIDAD}
     AND p.precio_compra = #{PRECIO_COMPRA}
