     AND p.composicion = #{COMPOSICION}
     AND p.prioridad = #{PRIORIDAD}
     AND p.ubicacion = #{UBICACION}
     AND p.activo_lotes = #{ACTIVO_LOTES}
     AND p.afecto_isc = #{AFECTO_ISC}
     AND p.tipo_tasa_isc = #{TIPO_TASA_ISC}
