left join stocks s on  s.producto_id = p.id and s.activo = 1 and s.borrado != 1
               and s.almacen_id = #{ALMACEN_ID}
left join detalle_unidad_productos d on d.producto_id = p.id
                                        and d.almacen_id = #{ALMACEN_ID}
