select COUNT(*)

from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join empresas e on e.id = p.empresa_id
inner join lotes l on p.id = l.producto_id and l.activo = 1 and l.borrado != 1
inner join almacens a on  l.almacen_id = a.id
inner join unidads u on u.id = l.unidad_id



     AND l.cantidad  >  0  and l.fecha_vencimiento is not null


     AND p.id = #{ID}
     AND p.codigo_producto like #{CODIGO}
     AND p.nombre like #{NOMBRE}
     AND p.composicion like #{COMPOSICION}
     AND p.ubicacion like #{UBICACION}
     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.stock_minimo = #{STOCK_MINIMO}
     AND p.precio_unidad = #{PRECIO_UNIDAD}
     AND p.precio_compra = #{PRECIO_COMPRA}
     AND p.fecha = #{FECHA}
     AND p.codigo_unidad = #{CODIGO_UNIDAD}
     AND p.codigo_producto = #{CODIGO_PRODUCTO}
     AND p.presentacion_id = #{PRESENTACION_ID}
     AND p.prioridad = #{PRIORIDAD}
     AND p.activo_lotes = #{ACTIVO_LOTES}
     AND p.afecto_isc = #{AFECTO_ISC}
     AND p.tipo_tasa_isc = #{TIPO_TASA_ISC}
     AND p.tasa_isc = #{TASA_ISC}
     AND p.afecto_igv = #{AFECTO_IGV}
     AND p.empresa_id = #{EMPRESA_ID}
     AND p.user_id = #{USER_ID}
     AND p.activo = #{ACTIVO}
     AND p.borrado = #{BORRADO}

     AND (p.nombre like #{BUSCAR} or p.codigo_unidad like #{BUSCAR} or  tp.tipo like #{BUSCAR} or  m.nombre like #{BUSCAR}
        or  pe.presentacion like #{BUSCAR}
        or p.composicion like #{BUSCAR} or p.ubicacion like #{BUSCAR} )

     AND p.id != #{NO_ID}
     AND p.borrado != #{NO_BORRADO}
     AND p.activo != #{NO_ACTIVO}


        and l.almacen_id= #{ALMACEN_ID}


     AND l.fecha_vencimiento  <=  #{FECHA_ACTUAL} 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 7 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 14 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 21 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 1 MONTH) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 2 MONTH) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 3 MONTH) 

     AND (l.fecha_vencimiento between  #{FECHA_INICIO} and #{FECHA_FINAL} ) 


order by l.fecha_vencimiento, p.nombre, p.codigo_unidad, p.id

select p.id,
p.nombre,
p.stock_minimo,
p.fecha,
p.codigo_unidad,
p.codigo_producto,
p.composicion,
p.prioridad,
p.ubicacion,
p.activo_lotes,
p.afecto_isc,
p.tipo_tasa_isc,
p.tasa_isc,
p.afecto_igv,
p.empresa_id,
p.user_id,
p.activo,
p.borrado,
p.created_at,
p.updated_at,
tp.id as tp_id,
tp.tipo as tp_tipo,
tp.tipo_producto_id as tp_tipo_producto_id,
tp.activo as tp_activo,
tp.borrado as tp_borrado,
m.id as m_id,
m.nombre as m_nombre,
m.activo as m_activo,
m.borrado as m_borrado,
pe.id as pe_id,
pe.presentacion as pe_presentacion,
pe.activo as pe_activo,
pe.borrado as pe_borrado,

a.id as a_id, a.nombre as a_nombre, a.direccion as a_direccion, a.activo as a_activo, a.borrado as a_borrado, a.codigo as a_codigo,

u.id as u_id,
u.nombre as u_nombre,
u.cantidad as u_cantidad,
u.abreviatura as u_abreviatura,
u.activo as u_activo,
u.borrado as u_borrado,
l.id as l_id,
l.nombre as l_nombre,
l.fecha_ingreso as l_fecha_ingreso,
l.fecha_vencimiento as l_fecha_vencimiento,
l.activo_vencimiento as l_activo_vencimiento,
l.producto_id as l_producto_id,
l.cantidad as l_cantidad,
l.observacion as l_observacion,
l.activo as l_activo,
l.borrado as l_borrado,
(l.cantidad * u.cantidad) as cantidad_total

from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join empresas e on e.id = p.empresa_id
inner join lotes l on p.id = l.producto_id and l.activo = 1 and l.borrado != 1
inner join almacens a on  l.almacen_id = a.id
inner join unidads u on u.id = l.unidad_id


     AND l.cantidad  >  0  and l.fecha_vencimiento is not null

     AND p.id = #{ID}
     AND p.codigo_producto like #{CODIGO}
     AND p.nombre like #{NOMBRE}
     AND p.composicion like #{COMPOSICION}
     AND p.ubicacion like #{UBICACION}
     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.stock_minimo = #{STOCK_MINIMO}
     AND p.precio_unidad = #{PRECIO_UNIDAD}
     AND p.precio_compra = #{PRECIO_COMPRA}
     AND p.fecha = #{FECHA}
     AND p.codigo_unidad = #{CODIGO_UNIDAD}
     AND p.codigo_producto = #{CODIGO_PRODUCTO}
     AND p.presentacion_id = #{PRESENTACION_ID}
     AND p.prioridad = #{PRIORIDAD}
     AND p.activo_lotes = #{ACTIVO_LOTES}
     AND p.afecto_isc = #{AFECTO_ISC}
     AND p.tipo_tasa_isc = #{TIPO_TASA_ISC}
     AND p.tasa_isc = #{TASA_ISC}
     AND p.afecto_igv = #{AFECTO_IGV}
     AND p.empresa_id = #{EMPRESA_ID}
     AND p.user_id = #{USER_ID}
     AND p.activo = #{ACTIVO}
     AND p.borrado = #{BORRADO}

     AND (p.nombre like #{BUSCAR} or p.codigo_unidad like #{BUSCAR} or  tp.tipo like #{BUSCAR} or  m.nombre like #{BUSCAR}
        or  pe.presentacion like #{BUSCAR}
        or p.composicion like #{BUSCAR} or p.ubicacion like #{BUSCAR} )

     AND p.id != #{NO_ID}
     AND p.borrado != #{NO_BORRADO}
     AND p.activo != #{NO_ACTIVO}


        and l.almacen_id= #{ALMACEN_ID}


     AND l.fecha_vencimiento  <=  #{FECHA_ACTUAL} 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 7 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 14 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 21 DAY) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 1 MONTH) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 2 MONTH) 

     AND l.fecha_vencimiento  <=  ADDDATE(#{FECHA_ACTUAL}, INTERVAL 3 MONTH) 

     AND (l.fecha_vencimiento between  #{FECHA_INICIO} and #{FECHA_FINAL} ) 


order by l.fecha_vencimiento, p.nombre, p.codigo_unidad, p.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

select COUNT(*)
from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join empresas e on e.id = p.empresa_id
inner join almacens a on a.empresa_id = e.id and a.id = #{ALMACEN_ID}
inner join stocks s on  s.producto_id = p.id and s.activo = 1 and s.borrado != 1 and s.almacen_id = a.id
inner join detalle_unidad_productos d on d.producto_id = p.id
and d.almacen_id = 0
and d.unidad_id = #{UNIDAD_ID}
and d.activo = 1
and d.borrado != 1
inner join unidads u on u.id = d.unidad_id
and u.activo = 1
and u.borrado != 1

     AND p.id = #{ID}
     AND p.codigo_producto like #{CODIGO}
     AND p.nombre like #{NOMBRE}
     AND p.composicion like #{COMPOSICION}
     AND p.ubicacion like #{UBICACION}
     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.stock_minimo = #{STOCK_MINIMO}
     AND p.precio_unidad = #{PRECIO_UNIDAD}
     AND p.precio_compra = #{PRECIO_COMPRA}
     AND p.fecha = #{FECHA}
     AND p.codigo_unidad = #{CODIGO_UNIDAD}
     AND p.codigo_producto = #{CODIGO_PRODUCTO}
     AND p.presentacion_id = #{PRESENTACION_ID}
     AND p.prioridad = #{PRIORIDAD}
     AND p.activo_lotes = #{ACTIVO_LOTES}
     AND p.afecto_isc = #{AFECTO_ISC}
     AND p.tipo_tasa_isc = #{TIPO_TASA_ISC}
     AND p.tasa_isc = #{TASA_ISC}
     AND p.afecto_igv = #{AFECTO_IGV}
     AND p.empresa_id = #{EMPRESA_ID}
     AND p.user_id = #{USER_ID}
     AND p.activo = #{ACTIVO}
     AND p.borrado = #{BORRADO}

     AND (p.nombre like #{BUSCAR} or p.codigo_unidad like #{BUSCAR} or  tp.tipo like #{BUSCAR} or  m.nombre like #{BUSCAR}
        or  pe.presentacion like #{BUSCAR}
        or p.composicion like #{BUSCAR} or p.ubicacion like #{BUSCAR} )

     AND p.id != #{NO_ID}
     AND p.borrado != #{NO_BORRADO}
     AND p.activo != #{NO_ACTIVO}

order by p.nombre, p.codigo_unidad, p.id

select p.id,
p.nombre,
p.stock_minimo,
p.fecha,
p.codigo_unidad,
p.codigo_producto,
p.composicion,
p.prioridad,
p.ubicacion,
p.activo_lotes,
p.afecto_isc,
p.tipo_tasa_isc,
p.tasa_isc,
p.afecto_igv,
p.empresa_id,
p.user_id,
p.precio_unidad,
p.precio_compra,
p.activo,
p.borrado,
p.created_at,
p.updated_at,
tp.id as tp_id,
tp.tipo as tp_tipo,
tp.tipo_producto_id as tp_tipo_producto_id,
tp.activo as tp_activo,
tp.borrado as tp_borrado,
m.id as m_id,
m.nombre as m_nombre,
m.activo as m_activo,
m.borrado as m_borrado,
pe.id as pe_id,
pe.presentacion as pe_presentacion,
pe.activo as pe_activo,
pe.borrado as pe_borrado,

a.id as a_id, a.nombre as a_nombre, a.direccion as a_direccion,
ifnull(s.id, 0) as s_id,
ifnull(s.cantidad, 0) as s_cantidad,
ifnull(d.id, 0) as d_id,

TRUNCATE(ifnull (d.precio, ( p.precio_unidad * u.cantidad)), 2) as precio_detalle_unidad,
TRUNCATE(ifnull (d.costo_compra, ( p.precio_compra * u.cantidad)), 2) as costo_detalle_unidad,
ifnull(d.codigo_unidad, 'NO CONFIG') as d_codigo_unidad,

ifnull(u.id, 0) as u_id,
ifnull(u.nombre, '') as u_nombre,
ifnull(u.cantidad, '') as u_cantidad,
ifnull(u.abreviatura, '') as u_abreviatura

from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join empresas e on e.id = p.empresa_id
inner join almacens a on a.empresa_id = e.id and a.id = #{ALMACEN_ID}
inner join stocks s on  s.producto_id = p.id and s.activo = 1 and s.borrado != 1 and s.almacen_id = a.id
inner join unidads u on u.id = #{UNIDAD_ID}
    and u.activo = 1
    and u.borrado != 1
left join detalle_unidad_productos d on d.producto_id = p.id
        and d.almacen_id = 0
        and d.unidad_id = #{UNIDAD_ID}
        and d.activo = 1
        and d.borrado != 1


     AND p.id = #{ID}
     AND p.codigo_producto like #{CODIGO}
     AND p.nombre like #{NOMBRE}
     AND p.composicion like #{COMPOSICION}
     AND p.ubicacion like #{UBICACION}
     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.stock_minimo = #{STOCK_MINIMO}
     AND p.precio_unidad = #{PRECIO_UNIDAD}
     AND p.precio_compra = #{PRECIO_COMPRA}
     AND p.fecha = #{FECHA}
     AND p.codigo_unidad = #{CODIGO_UNIDAD}
     AND p.codigo_producto = #{CODIGO_PRODUCTO}
     AND p.presentacion_id = #{PRESENTACION_ID}
     AND p.prioridad = #{PRIORIDAD}
     AND p.activo_lotes = #{ACTIVO_LOTES}
     AND p.afecto_isc = #{AFECTO_ISC}
     AND p.tipo_tasa_isc = #{TIPO_TASA_ISC}
     AND p.tasa_isc = #{TASA_ISC}
     AND p.afecto_igv = #{AFECTO_IGV}
     AND p.empresa_id = #{EMPRESA_ID}
     AND p.user_id = #{USER_ID}
     AND p.activo = #{ACTIVO}
     AND p.borrado = #{BORRADO}

     AND (p.nombre like #{BUSCAR} or p.codigo_unidad like #{BUSCAR} or  tp.tipo like #{BUSCAR} or  m.nombre like #{BUSCAR}
        or  pe.presentacion like #{BUSCAR}
        or p.composicion like #{BUSCAR} or p.ubicacion like #{BUSCAR} )

     AND p.id != #{NO_ID}
     AND p.borrado != #{NO_BORRADO}
     AND p.activo != #{NO_ACTIVO}

group by p.id
order by p.nombre, p.codigo_unidad, p.id
 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

