    and u.activo = 1
    and u.borrado != 1


     AND d.codigo_unidad = #{CODIGO_UNIDAD}

    AND (d.almacen_id = '0' OR d.almacen_id = a.id)
    AND d.activo = 1
    AND d.borrado != 1

