     AND v.empresa_id = #{EMPRESA_ID}
     AND v.user_id = #{USER_ID}
     AND v.activo = #{ACTIVO}
     AND v.borrado = #{BORRADO}
     AND v.tipo = #{TIPO}
     AND v.almacen_id = #{ALMACEN_ID}
     AND v.numero_venta = #{NUMERO_VENTA}

     AND v.id != #{NO_ID}
     AND v.borrado != #{NO_BORRADO}
     AND v.activo != #{NO_ACTIVO}

     AND c.id = #{CLI_CLIENTE_ID}
     AND c.nombre = #{CLI_NOMBRE}
     AND c.tipo_documento_id = #{CLI_TIPO_DOCUMENTO_ID}
     AND c.documento = #{CLI_DOCUMENTO}
     AND c.direccion = #{CLI_DIRECCION}
     AND c.telefono = #{CLI_TELEFONO}
     AND c.correo1 = #{CLI_CORREO1}
     AND c.correo2 = #{CLI_CORREO2}
     AND c.empresa_id = #{CLI_EMPRESA_ID}
     AND c.user_id = #{CLI_USER_ID}
     AND c.activo = #{CLI_ACTIVO}
     AND c.borrado = #{CLI_BORRADO}

     AND c.id != #{CLI_NO_ID}
     AND c.borrado != #{CLI_NO_BORRADO}
     AND c.activo != #{CLI_NO_ACTIVO}

     AND (c.documento like #{CLI_BUSCAR} or c.nombre like #{CLI_BUSCAR} )

     AND co.id = #{CO_ID}
     AND co.serie = #{CO_SERIE}
     AND co.numero = #{CO_NUMERO}
     AND ic.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND co.estado = #{CO_ESTADO}

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}

     AND (a.nombre like #{A_BUSCAR} )

     AND ( v.numero_venta = #{BUSCAR_GENERAL} OR
        c.documento like #{BUSCAR_GENERAL} or c.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(co.serie, co.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )



 order by v.id desc 
 order by v.fecha, v.hora, v.id

