v.total_monto,
v.total_afecto_isc,
v.igv,
v.isc,
v.estado,
v.user_id,
v.pagado,
v.hora,
v.empresa_id,
v.activo,
v.borrado,
v.created_at,
v.updated_at,
v.tipo,
v.almacen_id,
v.numero_venta,
v.cantidad_icbper,
v.monto_icbper,

c.id as cli_id,
c.nombre as cli_nombre,
c.tipo_documento_id as cli_tipo_documento_id,
c.documento as cli_documento,
c.direccion as cli_direccion,
c.telefono as cli_telefono,
c.correo1 as cli_correo1,
c.correo2 as cli_correo2,
c.user_id as cli_user_id,
c.empresa_id as cli_empresa_id,
c.activo as cli_activo,
c.borrado as cli_borrado,

co.id as co_id,
co.serie as co_serie,
co.numero as co_numero,
co.cantidad_digitos as co_cantidad_digitos,
co.init_comprobante_id as co_init_comprobante_id,
co.estado as co_estado,
co.empresa_id as co_empresa_id,
co.user_id as co_user_id,
co.activo as co_activo,
co.borrado as co_borrado,
co.almacen_id as co_almacen_id,

u.id as user_id,
u.name as user_name,
u.email as user_email,
