select
v.id,
v.fecha,
v.cliente_id,
v.comprobante_id,
v.subtotal_inafecto,
v.subtotal_exonerado,
v.subtotal_afecto,
v.total_monto,
v.total_afecto_isc,
v.igv,
v.isc,
v.estado,
v.user_id,
v.pagado,
v.hora,
v.empresa_id,
v.activo,
v.borrado,
v.created_at,
v.updated_at,
v.tipo,
v.almacen_id,
v.numero_venta,
v.cantidad_icbper,
v.monto_icbper,

c.id as cli_id,
c.nombre as cli_nombre,
c.tipo_documento_id as cli_tipo_documento_id,
c.documento as cli_documento,
c.direccion as cli_direccion,
c.telefono as cli_telefono,
c.correo1 as cli_correo1,
c.correo2 as cli_correo2,
c.user_id as cli_user_id,
c.empresa_id as cli_empresa_id,
c.activo as cli_activo,
c.borrado as cli_borrado,

co.id as co_id,
co.serie as co_serie,
co.numero as co_numero,
co.cantidad_digitos as co_cantidad_digitos,
co.init_comprobante_id as co_init_comprobante_id,
co.estado as co_estado,
co.empresa_id as co_empresa_id,
co.user_id as co_user_id,
co.activo as co_activo,
co.borrado as co_borrado,
co.almacen_id as co_almacen_id,

u.id as user_id,
u.name as user_name,
u.email as user_email,
u.tipo_user_id as user_tipo_user_id,
u.user_id as user_user_id,
u.empresa_id as user_empresa_id,
u.activo as user_activo,
u.borrado as user_borrado,
u.almacen_id as user_almacen_id,

du.id as du_id,
du.nombres as du_nombres,
du.apellido_paterno as du_apellido_paterno,
du.apellido_materno as du_apellido_materno,
du.direccion as du_direccion,
du.telefono as du_telefono,
du.tipo_documento_id as du_tipo_documento_id,
du.documento as du_documento,
du.email as du_email,
du.user_id as du_user_id,
du.user_gestiona_id as du_user_gestiona_id,
du.empresa_id as du_empresa_id,
du.activo as du_activo,
du.borrado as du_borrado,

a.id as al_id,
a.nombre as al_nombre,
a.direccion as al_direccion,
a.empresa_id as al_empresa_id,
a.user_id as al_user_id,
a.activo as al_activo,
a.borrado as al_borrado,
a.distrito_id as al_distrito_id,
a.codigo as al_codigo,

TRUNCATE(@a:= v.total_monto,2) AS importe_total,
TRUNCATE(@b:=(SELECT IFNULL(SUM(cv.`importe`),0.00) As montoCobrado FROM `cobro_ventas` cv WHERE cv.`venta_id`=v.`id` and cv.borrado !='1'),2) AS monto_cobrado,
TRUNCATE(@a-@b,2) AS monto_por_cobrar,

d.id as dv_id,
d.venta_id as dv_venta_id,
d.producto_id as dv_producto_id,
d.precio_venta as dv_precio_venta,
d.precio_compra as dv_precio_compra,
d.cantidad as dv_cantidad,
d.almacen_id as dv_almacen_id,
d.es_grabado as dv_es_grabado,
d.es_isc as dv_es_isc,
d.descuento as dv_descuento,
d.tipo_descuento as dv_tipo_descuento,
d.cantidad_real as dv_cantidad_real,
d.unidad as dv_unidad,
d.precio_total as dv_precio_total,
d.lote_id as dv_lote_id,
d.user_id as dv_user_id,
d.empresa_id as dv_empresa_id,
d.activo as dv_activo,
d.borrado as dv_borrado,
d.created_at as dv_created_at,
d.updated_at as dv_updated_at,

p.id as p_id,
p.nombre as p_nombre,
p.tipo_producto_id as p_tipo_producto_id,
p.marca_id as p_marca_id,
p.stock_minimo as p_stock_minimo,
p.precio_unidad as p_precio_unidad,
p.precio_compra as p_precio_compra,
p.fecha as p_fecha,
p.codigo_unidad as p_codigo_unidad,
p.codigo_producto as p_codigo_producto,
p.presentacion_id as p_presentacion_id,
p.composicion as p_composicion,
p.prioridad as p_prioridad,
p.ubicacion as p_ubicacion,
p.activo_lotes as p_activo_lotes,
p.user_id as p_user_id,
p.empresa_id as p_empresa_id,
p.activo as p_activo,
p.borrado as p_borrado,
p.created_at as p_created_at,
p.updated_at as p_updated_at,
p.afecto_isc as p_afecto_isc,
p.tipo_tasa_isc as p_tipo_tasa_isc,
p.tasa_isc as p_tasa_isc,
p.afecto_igv as p_afecto_igv,

tp.id as tp_id,
tp.tipo as tp_tipo,
tp.tipo_producto_id as tp_tipo_producto_id,
tp.activo as tp_activo,
tp.borrado as tp_borrado,
m.id as m_id,
m.nombre as m_nombre,
m.activo as m_activo,
m.borrado as m_borrado,
pe.id as pe_id,
pe.presentacion as pe_presentacion,
pe.activo as pe_activo,
pe.borrado as pe_borrado,

ifnull(l.id, 0) as l_id,
ifnull(l.orden, '') as l_orden,
ifnull(l.nombre, '') as l_nombre,
ifnull(l.fecha_ingreso, null) as l_fecha_ingreso,
ifnull(l.fecha_vencimiento, null) as l_fecha_vencimiento,
ifnull(l.activo_vencimiento, null) as l_activo_vencimiento,
ifnull(l.producto_id, '') as l_producto_id

from ventas v
inner join almacens a on a.id = v.almacen_id
inner join users u on u.id = v.user_id
inner join detalle_ventas d on v.id = d.venta_id
inner join productos p on d.producto_id = p.id
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
left join lotes l on d.lote_id = l.id
left join datos_users du on du.user_id = u.id
left join clientes c on c.id = v.cliente_id
left join comprobantes co on co.id = v.comprobante_id
left join init_comprobantes ic on ic.id = co.init_comprobante_id

     AND v.id = #{ID}
     AND v.fecha = #{FECHA}

     AND v.fecha  >=   #{FECHA_INI}
     AND v.fecha  <=   #{FECHA_FIN}

     AND v.cliente_id = #{CLIENTE_ID}
     AND v.comprobante_id = #{COMPROBANTE_ID}
     AND v.subtotal_inafecto = #{SUBTOTAL_INAFECTO}
     AND v.subtotal_exonerado = #{SUBTOTAL_EXONERADO}
     AND v.subtotal_afecto = #{SUBTOTAL_AFECTO}
     AND v.igv = #{IGV}
     AND v.estado = #{ESTADO}
     AND v.pagado = #{PAGADO}
     AND v.hora = #{HORA}
     AND v.empresa_id = #{EMPRESA_ID}
     AND v.user_id = #{USER_ID}
     AND v.activo = #{ACTIVO}
     AND v.borrado = #{BORRADO}
     AND v.tipo = #{TIPO}
     AND v.almacen_id = #{ALMACEN_ID}
     AND v.numero_venta = #{NUMERO_VENTA}

     AND v.id != #{NO_ID}
     AND v.borrado != #{NO_BORRADO}
     AND v.activo != #{NO_ACTIVO}

     AND c.id = #{CLI_CLIENTE_ID}
     AND c.nombre like #{CLI_NOMBRE}
     AND c.tipo_documento_id = #{CLI_TIPO_DOCUMENTO_ID}
     AND c.documento = #{CLI_DOCUMENTO}
     AND c.direccion = #{CLI_DIRECCION}
     AND c.telefono = #{CLI_TELEFONO}
     AND c.correo1 = #{CLI_CORREO1}
     AND c.correo2 = #{CLI_CORREO2}
     AND c.empresa_id = #{CLI_EMPRESA_ID}
     AND c.user_id = #{CLI_USER_ID}
     AND c.activo = #{CLI_ACTIVO}
     AND c.borrado = #{CLI_BORRADO}

     AND c.id != #{CLI_NO_ID}
     AND c.borrado != #{CLI_NO_BORRADO}
     AND c.activo != #{CLI_NO_ACTIVO}

     AND (c.documento like #{CLI_BUSCAR} or c.nombre like #{CLI_BUSCAR} )

     AND co.id = #{CO_ID}
     AND co.serie = #{CO_SERIE}
     AND co.numero = #{CO_NUMERO}
     AND ic.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND co.estado = #{CO_ESTADO}

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}

     AND (a.nombre like #{A_BUSCAR} )

     AND ( v.numero_venta like #{BUSCAR_GENERAL} OR
        c.documento like #{BUSCAR_GENERAL} or c.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(co.serie,'-', co.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )


     AND d.id = #{ID}
     AND d.venta_id = #{VENTA_ID}
     AND d.producto_id = #{PRODUCTO_ID}
     AND d.precio_venta = #{PRECIO_VENTA}
     AND d.precio_compra = #{PRECIO_COMPRA}
     AND d.cantidad = #{CANTIDAD}
     AND d.almacen_id = #{ALMACEN_ID}
     AND d.es_grabado = #{ES_GRABADO}
     AND d.descuento = #{DESCUENTO}
     AND d.tipo_descuento = #{TIPO_DESCUENTO}
     AND d.cantidad_real = #{CANTIDAD_REAL}
     AND d.unidad = #{UNIDAD}
     AND d.lote_id = #{LOTE_ID}
     AND d.empresa_id = #{EMPRESA_ID}
     AND d.user_id = #{USER_ID}
     AND d.activo = #{ACTIVO}
     AND d.borrado = #{BORRADO}

     AND d.id != #{NO_ID}
     AND d.borrado != #{NO_BORRADO}
     AND d.activo != #{NO_ACTIVO}


 order by v.id desc 
 order by v.fecha, v.hora, v.id 
, d.venta_id, d.id

 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

select COUNT(*)
from ventas v
inner join almacens a on a.id = v.almacen_id
inner join users u on u.id = v.user_id
inner join detalle_ventas d on v.id = d.venta_id
inner join productos p on d.producto_id = p.id
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
left join lotes l on d.lote_id = l.id
left join datos_users du on du.user_id = u.id
left join clientes c on c.id = v.cliente_id
left join comprobantes co on co.id = v.comprobante_id
left join init_comprobantes ic on ic.id = co.init_comprobante_id

     AND v.id = #{ID}
     AND v.fecha = #{FECHA}

     AND v.fecha  >=   #{FECHA_INI}
     AND v.fecha  <=   #{FECHA_FIN}

     AND v.cliente_id = #{CLIENTE_ID}
     AND v.comprobante_id = #{COMPROBANTE_ID}
     AND v.subtotal_inafecto = #{SUBTOTAL_INAFECTO}
     AND v.subtotal_exonerado = #{SUBTOTAL_EXONERADO}
     AND v.subtotal_afecto = #{SUBTOTAL_AFECTO}
     AND v.igv = #{IGV}
     AND v.estado = #{ESTADO}
     AND v.pagado = #{PAGADO}
     AND v.hora = #{HORA}
     AND v.empresa_id = #{EMPRESA_ID}
     AND v.user_id = #{USER_ID}
     AND v.activo = #{ACTIVO}
     AND v.borrado = #{BORRADO}
     AND v.tipo = #{TIPO}
     AND v.almacen_id = #{ALMACEN_ID}
     AND v.numero_venta = #{NUMERO_VENTA}

     AND v.id != #{NO_ID}
     AND v.borrado != #{NO_BORRADO}
     AND v.activo != #{NO_ACTIVO}

     AND c.id = #{CLI_CLIENTE_ID}
     AND c.nombre like #{CLI_NOMBRE}
     AND c.tipo_documento_id = #{CLI_TIPO_DOCUMENTO_ID}
     AND c.documento = #{CLI_DOCUMENTO}
     AND c.direccion = #{CLI_DIRECCION}
     AND c.telefono = #{CLI_TELEFONO}
     AND c.correo1 = #{CLI_CORREO1}
     AND c.correo2 = #{CLI_CORREO2}
     AND c.empresa_id = #{CLI_EMPRESA_ID}
     AND c.user_id = #{CLI_USER_ID}
     AND c.activo = #{CLI_ACTIVO}
     AND c.borrado = #{CLI_BORRADO}

     AND c.id != #{CLI_NO_ID}
     AND c.borrado != #{CLI_NO_BORRADO}
     AND c.activo != #{CLI_NO_ACTIVO}

     AND (c.documento like #{CLI_BUSCAR} or c.nombre like #{CLI_BUSCAR} )

     AND co.id = #{CO_ID}
     AND co.serie = #{CO_SERIE}
     AND co.numero = #{CO_NUMERO}
     AND ic.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND co.estado = #{CO_ESTADO}

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}

     AND (a.nombre like #{A_BUSCAR} )

     AND ( v.numero_venta like #{BUSCAR_GENERAL} OR
        c.documento like #{BUSCAR_GENERAL} or c.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(co.serie,'-', co.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )


     AND d.id = #{ID}
     AND d.venta_id = #{VENTA_ID}
     AND d.producto_id = #{PRODUCTO_ID}
     AND d.precio_venta = #{PRECIO_VENTA}
     AND d.precio_compra = #{PRECIO_COMPRA}
     AND d.cantidad = #{CANTIDAD}
     AND d.almacen_id = #{ALMACEN_ID}
     AND d.es_grabado = #{ES_GRABADO}
     AND d.descuento = #{DESCUENTO}
     AND d.tipo_descuento = #{TIPO_DESCUENTO}
     AND d.cantidad_real = #{CANTIDAD_REAL}
     AND d.unidad = #{UNIDAD}
     AND d.lote_id = #{LOTE_ID}
     AND d.empresa_id = #{EMPRESA_ID}
     AND d.user_id = #{USER_ID}
     AND d.activo = #{ACTIVO}
     AND d.borrado = #{BORRADO}

     AND d.id != #{NO_ID}
     AND d.borrado != #{NO_BORRADO}
     AND d.activo != #{NO_ACTIVO}


 order by v.id desc 
 order by v.fecha, v.hora, v.id 
, d.venta_id, d.id

select p.id,
p.nombre,
p.stock_minimo,
p.precio_unidad,
p.precio_compra,
p.fecha,
p.codigo_unidad,
p.codigo_producto,
p.composicion,
p.prioridad,
p.ubicacion,
p.activo_lotes,
p.afecto_isc,
p.tipo_tasa_isc,
p.tasa_isc,
p.afecto_igv,
p.empresa_id,
p.user_id,
p.activo,
p.borrado,
p.created_at,
p.updated_at,
tp.id as tp_id,
tp.tipo as tp_tipo,
tp.tipo_producto_id as tp_tipo_producto_id,
tp.activo as tp_activo,
tp.borrado as tp_borrado,
m.id as m_id,
m.nombre as m_nombre,
m.activo as m_activo,
m.borrado as m_borrado,
pe.id as pe_id,
pe.presentacion as pe_presentacion,
pe.activo as pe_activo,
pe.borrado as pe_borrado,
sum(d.cantidad * d.cantidad_real) as cantidad,
count(v.id) as ventas,
sum(d.precio_total) as importe

from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join detalle_ventas d on d.producto_id = p.id
inner join ventas v on v.id = d.venta_id
inner join almacens a on a.id = v.almacen_id
inner join users u on u.id = v.user_id
left join datos_users du on du.user_id = u.id
left join clientes c on c.id = v.cliente_id
left join comprobantes co on co.id = v.comprobante_id
left join init_comprobantes ic on ic.id = co.init_comprobante_id

     AND v.id = #{ID}
     AND v.fecha = #{FECHA}

     AND v.fecha  >=   #{FECHA_INI}
     AND v.fecha  <=   #{FECHA_FIN}

     AND v.cliente_id = #{CLIENTE_ID}
     AND v.comprobante_id = #{COMPROBANTE_ID}
     AND v.subtotal_inafecto = #{SUBTOTAL_INAFECTO}
     AND v.subtotal_exonerado = #{SUBTOTAL_EXONERADO}
     AND v.subtotal_afecto = #{SUBTOTAL_AFECTO}
     AND v.igv = #{IGV}
     AND v.estado = #{ESTADO}
     AND v.pagado = #{PAGADO}
     AND v.hora = #{HORA}
     AND v.empresa_id = #{EMPRESA_ID}
     AND v.user_id = #{USER_ID}
     AND v.activo = #{ACTIVO}
     AND v.borrado = #{BORRADO}
     AND v.tipo = #{TIPO}
     AND v.almacen_id = #{ALMACEN_ID}
     AND v.numero_venta = #{NUMERO_VENTA}

     AND v.id != #{NO_ID}
     AND v.borrado != #{NO_BORRADO}
     AND v.activo != #{NO_ACTIVO}

     AND c.id = #{CLI_CLIENTE_ID}
     AND c.nombre like #{CLI_NOMBRE}
     AND c.tipo_documento_id = #{CLI_TIPO_DOCUMENTO_ID}
     AND c.documento = #{CLI_DOCUMENTO}
     AND c.direccion = #{CLI_DIRECCION}
     AND c.telefono = #{CLI_TELEFONO}
     AND c.correo1 = #{CLI_CORREO1}
     AND c.correo2 = #{CLI_CORREO2}
     AND c.empresa_id = #{CLI_EMPRESA_ID}
     AND c.user_id = #{CLI_USER_ID}
     AND c.activo = #{CLI_ACTIVO}
     AND c.borrado = #{CLI_BORRADO}

     AND c.id != #{CLI_NO_ID}
     AND c.borrado != #{CLI_NO_BORRADO}
     AND c.activo != #{CLI_NO_ACTIVO}

     AND (c.documento like #{CLI_BUSCAR} or c.nombre like #{CLI_BUSCAR} )

     AND co.id = #{CO_ID}
     AND co.serie = #{CO_SERIE}
     AND co.numero = #{CO_NUMERO}
     AND ic.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND co.estado = #{CO_ESTADO}

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}

     AND (a.nombre like #{A_BUSCAR} )

     AND ( v.numero_venta like #{BUSCAR_GENERAL} OR
        c.documento like #{BUSCAR_GENERAL} or c.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(co.serie,'-', co.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )


     AND d.id = #{ID}
     AND d.venta_id = #{VENTA_ID}
     AND d.producto_id = #{PRODUCTO_ID}
     AND d.precio_venta = #{PRECIO_VENTA}
     AND d.precio_compra = #{PRECIO_COMPRA}
     AND d.cantidad = #{CANTIDAD}
     AND d.almacen_id = #{ALMACEN_ID}
     AND d.es_grabado = #{ES_GRABADO}
     AND d.descuento = #{DESCUENTO}
     AND d.tipo_descuento = #{TIPO_DESCUENTO}
     AND d.cantidad_real = #{CANTIDAD_REAL}
     AND d.unidad = #{UNIDAD}
     AND d.lote_id = #{LOTE_ID}
     AND d.empresa_id = #{EMPRESA_ID}
     AND d.user_id = #{USER_ID}
     AND d.activo = #{ACTIVO}
     AND d.borrado = #{BORRADO}

     AND d.id != #{NO_ID}
     AND d.borrado != #{NO_BORRADO}
     AND d.activo != #{NO_ACTIVO}

     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.presentacion_id = #{PRESENTACION_ID}


group by p.id order by 27 desc, 28 desc

 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

select count(*) from
(
select p.id
from productos p
inner join tipo_productos tp on p.tipo_producto_id = tp.id
inner join marcas m on p.marca_id=m.id
inner join presentacions pe on p.presentacion_id=pe.id
inner join detalle_ventas d on d.producto_id = p.id
inner join ventas v on v.id = d.venta_id
inner join almacens a on a.id = v.almacen_id
inner join users u on u.id = v.user_id
left join datos_users du on du.user_id = u.id
left join clientes c on c.id = v.cliente_id
left join comprobantes co on co.id = v.comprobante_id
left join init_comprobantes ic on ic.id = co.init_comprobante_id

     AND v.id = #{ID}
     AND v.fecha = #{FECHA}

     AND v.fecha  >=   #{FECHA_INI}
     AND v.fecha  <=   #{FECHA_FIN}

     AND v.cliente_id = #{CLIENTE_ID}
     AND v.comprobante_id = #{COMPROBANTE_ID}
     AND v.subtotal_inafecto = #{SUBTOTAL_INAFECTO}
     AND v.subtotal_exonerado = #{SUBTOTAL_EXONERADO}
     AND v.subtotal_afecto = #{SUBTOTAL_AFECTO}
     AND v.igv = #{IGV}
     AND v.estado = #{ESTADO}
     AND v.pagado = #{PAGADO}
     AND v.hora = #{HORA}
     AND v.empresa_id = #{EMPRESA_ID}
     AND v.user_id = #{USER_ID}
     AND v.activo = #{ACTIVO}
     AND v.borrado = #{BORRADO}
     AND v.tipo = #{TIPO}
     AND v.almacen_id = #{ALMACEN_ID}
     AND v.numero_venta = #{NUMERO_VENTA}

     AND v.id != #{NO_ID}
     AND v.borrado != #{NO_BORRADO}
     AND v.activo != #{NO_ACTIVO}

     AND c.id = #{CLI_CLIENTE_ID}
     AND c.nombre like #{CLI_NOMBRE}
     AND c.tipo_documento_id = #{CLI_TIPO_DOCUMENTO_ID}
     AND c.documento = #{CLI_DOCUMENTO}
     AND c.direccion = #{CLI_DIRECCION}
     AND c.telefono = #{CLI_TELEFONO}
     AND c.correo1 = #{CLI_CORREO1}
     AND c.correo2 = #{CLI_CORREO2}
     AND c.empresa_id = #{CLI_EMPRESA_ID}
     AND c.user_id = #{CLI_USER_ID}
     AND c.activo = #{CLI_ACTIVO}
     AND c.borrado = #{CLI_BORRADO}

     AND c.id != #{CLI_NO_ID}
     AND c.borrado != #{CLI_NO_BORRADO}
     AND c.activo != #{CLI_NO_ACTIVO}

     AND (c.documento like #{CLI_BUSCAR} or c.nombre like #{CLI_BUSCAR} )

     AND co.id = #{CO_ID}
     AND co.serie = #{CO_SERIE}
     AND co.numero = #{CO_NUMERO}
     AND ic.tipo_comprobante_id = #{CO_TIPO_COMPROBANTE_ID}
     AND co.estado = #{CO_ESTADO}

     AND u.id = #{U_ID}
     AND u.name = #{U_NAME}
     AND u.email = #{U_EMAIL}
     AND u.tipo_user_id = #{U_TIPO_USER_ID}

     AND (u.name like #{U_BUSCAR} or u.email like #{U_BUSCAR} )

     AND du.id = #{DU_ID}
     AND du.nombres = #{DU_NOMBRES}
     AND du.apellido_paterno = #{DU_APELLIDO_PATERNO}
     AND du.apellido_materno = #{DU_APELLIDO_MATERNO}
     AND du.documento = #{DU_DOCUMENTO}

     AND (du.nombres like #{DU_BUSCAR} or du.apellido_paterno like #{DU_BUSCAR} or du.apellido_materno like #{DU_BUSCAR} or du.documento like #{DU_BUSCAR} )

     AND a.id = #{A_ID}
     AND a.nombre = #{A_NOMBRE}

     AND (a.nombre like #{A_BUSCAR} )

     AND ( v.numero_venta like #{BUSCAR_GENERAL} OR
        c.documento like #{BUSCAR_GENERAL} or c.nombre like #{BUSCAR_GENERAL} OR
        CONCAT(co.serie,'-', co.numero) like #{BUSCAR_GENERAL} OR
        u.name like #{BUSCAR_GENERAL} or u.email like #{BUSCAR_GENERAL} OR
        du.nombres like #{BUSCAR_GENERAL} or du.apellido_paterno like #{BUSCAR_GENERAL} or du.apellido_materno like #{BUSCAR_GENERAL} or du.documento like #{BUSCAR_GENERAL} )


     AND d.id = #{ID}
     AND d.venta_id = #{VENTA_ID}
     AND d.producto_id = #{PRODUCTO_ID}
     AND d.precio_venta = #{PRECIO_VENTA}
     AND d.precio_compra = #{PRECIO_COMPRA}
     AND d.cantidad = #{CANTIDAD}
     AND d.almacen_id = #{ALMACEN_ID}
     AND d.es_grabado = #{ES_GRABADO}
     AND d.descuento = #{DESCUENTO}
     AND d.tipo_descuento = #{TIPO_DESCUENTO}
     AND d.cantidad_real = #{CANTIDAD_REAL}
     AND d.unidad = #{UNIDAD}
     AND d.lote_id = #{LOTE_ID}
     AND d.empresa_id = #{EMPRESA_ID}
     AND d.user_id = #{USER_ID}
     AND d.activo = #{ACTIVO}
     AND d.borrado = #{BORRADO}

     AND d.id != #{NO_ID}
     AND d.borrado != #{NO_BORRADO}
     AND d.activo != #{NO_ACTIVO}

     AND p.tipo_producto_id = #{TIPO_PRODUCTO_ID}
     AND p.marca_id = #{MARCA_ID}
     AND p.presentacion_id = #{PRESENTACION_ID}

group by p.id order by p.id

) as T

