     AND cov.borrado != #{NO_BORRADO}
     AND cov.activo != #{NO_ACTIVO}

     AND v.almacen_id = #{ALMACEN_ID}

     AND cov.importe  >  #{MAYOR_MIN_IMPORTE}


 order by v.id desc 
 order by v.fecha, v.hora, v.id 
, cov.fecha, cov.id

 LIMIT #{LIMIT}
 OFFSET #{OFFSET}

